import React from 'react';
import { useExpense } from '../context/ExpenseContext';

const Upload = () => {
  const { handleFileUpload } = useExpense();

  const onChange = async (e) => {
    const files = e.target.files;
    if (!files || files.length === 0) return;
    await handleFileUpload(files);
    e.target.value = '';
  };

  return (
    <div>
      <h2 className="text-2xl font-semibold text-gray-900">Upload Receipts</h2>
      <p className="mt-2 text-gray-700">Upload receipt images or PDFs to extract transactions.</p>
      <div className="mt-4">
        <input type="file" accept="image/*,application/pdf" multiple onChange={onChange} />
      </div>
    </div>
  );
};

export default Upload;
