import React from 'react';
import { useExpense } from '../context/ExpenseContext';

const Dashboard = () => {
  const { analytics } = useExpense();

  return (
    <div>
      <h2 className="text-2xl font-semibold text-gray-900">Dashboard</h2>
      <p className="mt-2 text-gray-700">Welcome back! Here's a quick overview.</p>
      {analytics?.summary && (
        <div className="mt-4 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
          <div className="rounded-lg bg-white p-4 shadow">
            <div className="text-sm text-gray-500">Total Spent</div>
            <div className="text-xl font-bold">${analytics.summary.total_spent.toFixed(2)}</div>
          </div>
          <div className="rounded-lg bg-white p-4 shadow">
            <div className="text-sm text-gray-500">Transactions</div>
            <div className="text-xl font-bold">{analytics.summary.total_transactions}</div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
