import React, { useMemo } from 'react';
import { useExpense } from '../context/ExpenseContext';
import { PieChart, Pie, Cell, ResponsiveContainer } from 'recharts';

const COLORS = ['#3b82f6', '#ef4444', '#10b981', '#f59e0b', '#8b5cf6', '#06b6d4'];

const Analytics = () => {
  const { selectedTransactions } = useExpense();

  const categoryData = useMemo(() => {
    const map = new Map();
    selectedTransactions.forEach((t) => {
      const key = t.category || 'Uncategorized';
      const prev = map.get(key) || 0;
      map.set(key, prev + (Number(t.amount) || 0));
    });
    return Array.from(map.entries()).map(([category, total]) => ({ name: category, value: total }));
  }, [selectedTransactions]);

  return (
    <div>
      <h2 className="text-2xl font-semibold text-gray-900 dark:text-gray-100">Analytics</h2>
      <div className="mt-6 grid grid-cols-1 gap-6 lg:grid-cols-3">
        <div className="card lg:col-span-2">
          <div className="h-80 w-full">
            <ResponsiveContainer>
              <PieChart>
                <Pie data={categoryData} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={110} label>
                  {categoryData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
              </PieChart>
            </ResponsiveContainer>
          </div>
        </div>
        <div className="card">
          <h3 className="text-sm font-medium text-gray-700">Categories</h3>
          <div className="mt-4 space-y-2">
            {categoryData.length === 0 && (
              <div className="text-sm text-gray-500">No data</div>
            )}
            {categoryData.map((c, i) => (
              <div key={c.name} className="flex items-center justify-between">
                <div className="flex items-center gap-2">
                  <span className="inline-block h-3 w-3 rounded-sm" style={{ backgroundColor: COLORS[i % COLORS.length] }} />
                  <span className="text-sm text-gray-700">{c.name}</span>
                </div>
                <span className="text-sm font-medium text-gray-900">${c.value.toFixed ? c.value.toFixed(2) : c.value}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Analytics;
